import React from 'react';
import firebase from './firebase';
import './App.css';

import {Button,Row,Container,Col,Form,Navbar,Table, NavbarBrand} from 'react-bootstrap';
import Note from './components/note';

function App() {

  const [tasks, setTasks] = React.useState([]); 
  const [newTask, setnewTask] = React.useState(''); 
  const [updateTask, setupdateTask] = React.useState(''); 

  React.useEffect(() => {
    const fetchData = async () => {

      const db = firebase.firestore();

      db.collection("tasks")
      .onSnapshot(function(data) { 
        console.log(data)
        setTasks(data.docs.map(doc => ({ ...doc.data(), id: doc.id }))); 
      });
    };
    fetchData(); 
}, []);

const onCreate = () => {

    const db = firebase.firestore();
    db.collection("tasks").add({ name: newTask }); 
  };

  function onDelete (id) { 

    const db = firebase.firestore();
    db.collection('tasks').doc(id).delete();
  }

  const onUpdate = (id) => { 

    const db = firebase.firestore();
    db.collection('tasks').doc(id).set({name:updateTask});
  }

  return (
    <Note/>
  );

}
export default App;
