import React from 'react'
import firebase from '../firebase';

import { Button, Row, Container, Col, Form, Navbar, Table, NavbarBrand } from 'react-bootstrap';

function Note() {

  const [tasks, setTasks] = React.useState([]);
  const [newTask, setnewTask] = React.useState('');
  const [updateTask, setupdateTask] = React.useState('');

  React.useEffect(() => {
    const fetchData = async () => {

      const db = firebase.firestore();

      db.collection("stocks")
        .onSnapshot(function (data) {
          console.log(data)
          setTasks(data.docs.map(doc => ({ ...doc.data(), id: doc.id })));
        });
    };
    fetchData();
  }, []);

  const onCreate = () => {

    const db = firebase.firestore();
    db.collection("tasks").add({ name: newTask });
  };

  function onDelete(id) {

    const db = firebase.firestore();
    db.collection('tasks').doc(id).delete();
  }

  const onUpdate = (id) => {

    const db = firebase.firestore();
    db.collection('tasks').doc(id).set({ name: updateTask });
  }

  //

  const checkVar = () => {

    console.log({ tasks });
  }

  return (
    <div>
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="#home">
          Add or Edit note
        </Navbar.Brand>
      </Navbar>

      <br></br>
      <Container>

        <Row>
          <Col>
            <h2>Add new Stock</h2>
            {/* <Form>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Control type="text" value={newTask} onChange={e => setnewTask(e.target.value)} />
              </Form.Group>
              <Button variant="primary" onClick={onCreate}>Create Task</Button>
            </Form> */}

            <hr/>

            <Table striped bodered="true" hover variant="dark">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Task Name</th>
                  <th>Delete Task</th>
                  <th>Update Task</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map(spell => (
                  <tr key={spell.id}>
                    <td>{spell.id}</td>
                    <td>{spell.name}</td>
                    <td> <Button variant="danger" onClick={() => onDelete(spell.id)}>{spell.id}</Button></td>

                    <td>
                      <input type="text" className=" " placeholder={spell.name} onChange={e => setupdateTask(e.target.value)} placeholder={spell.name}></input>

                      <Button className="text-white ml-4" variant="warning" onClick={() => onUpdate(spell.id)}>Update Task</Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>


          </Col>
        </Row>
        <Button className="text-white ml-4" variant="warning" onClick={() => checkVar()}> Check Var </Button>

        <br></br>

        <Row>
          <Col md={6}>

            <Table hover>
              <tbody>
                <tr>
                  <td>
                    <Button variant="warning">INTC</Button>
                  </td>
                  <td>
                    <p>Intel Corporation</p>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Button variant="warning">MSFT</Button>
                  </td>
                  <td>
                    <p>Microsoft Corporation</p>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Button variant="warning">DIS</Button>
                  </td>
                  <td>
                    <p>The Walt Disney Company</p>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Button variant="warning">BA</Button>
                  </td>
                  <td>
                    <p>The Boeing Company</p>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Button variant="warning">MCD</Button>
                  </td>
                  <td>
                    <p>McDonald's Corporation</p>
                  </td>
                </tr>
              </tbody>
            </Table>

          </Col>
        </Row>
      </Container>
    </div>
  )
}

export default Note;
